var Timer=function(){this.timePaused=this.lastUpdate=this.pauseTime=this.elapsedTime=0;this.running=!1;this.multiplier=1};Timer.prototype.update=function(){if(this.running){var a=(Date.now()-this.lastUpdate-this.timePaused)*this.multiplier;this.lastUpdate=Date.now();this.timePaused=0;this.elapsedTime+=a}else a=0;return a};Timer.prototype.start=function(){this.elapsedTime=0;this.pauseTime=Date.now();this.lastUpdate=Date.now();this.timePaused=0;this.running=!0};
Timer.prototype.stop=function(){this.lastUpdate=this.pauseTime=this.elapsedTime=0;this.running=!1};Timer.prototype.resume=function(){this.running||(this.timePaused+=Date.now()-this.pauseTime,this.running=!0)};Timer.prototype.pause=function(){this.running&&(this.pauseTime=Date.now(),this.running=!1)};Timer.prototype.isRunning=function(){return this.running};Timer.prototype.getElapsedMillis=function(){return this.elapsedTime};
Timer.prototype.getElapsedSeconds=function(){return this.getElapsedMillis()/1E3};Timer.prototype.setMultiplier=function(a){this.multiplier=a};Timer.prototype.getMultiplier=function(){return this.multiplier};
